import React, { useState } from 'react';
import { Search, RefreshCw, Sparkles, TrendingUp, Award, AlertCircle } from 'lucide-react';
import PromptGrid from './components/PromptGrid';
import FontRecommendations from './components/FontRecommendations';
import LoadingSpinner from './components/LoadingSpinner';
import ScoreSummary from './components/ScoreSummary';
import { fetchFontRecommendationsStream } from './services/api';

function App() {
    const [selectedPrompt, setSelectedPrompt] = useState('');
    const [recommendations, setRecommendations] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [scores, setScores] = useState([]);
    const [progress, setProgress] = useState(0);

    const handlePromptClick = async (prompt) => {
        setSelectedPrompt(prompt);
        setLoading(true);
        setError('');
        setRecommendations([]);
        setScores([]);
        setProgress(0);

        try {
            const response = await fetchFontRecommendationsStream(
                prompt,
                "",
                (data) => {
                    // Handle progress updates
                    if (data.progress !== undefined) {
                        setProgress(data.progress);
                    }

                    // If we have partial results, show them
                    if (data.results?.recommendations) {
                        setRecommendations(data.results.recommendations.slice(0, 20));
                    }
                }
            );

            console.log('Final API Response:', response);
            console.log('Response status:', response.status);
            console.log('Response results:', response.results);
            console.log('Recommendations count:', response.results?.recommendations?.length || 0);

            // Check for API error status
            if (response.status === 'error') {
                console.log('API returned error status');
                setError('Something went wrong with the font recommendation service. Please try again later.');
                return;
            }

            // Check if status is complete and we have recommendations
            if (response.status === 'complete') {
                console.log('API returned complete status');
                if (response.results && response.results.recommendations && response.results.recommendations.length > 0) {
                    console.log('Found recommendations, processing...');
                    const topRecommendations = response.results.recommendations.slice(0, 20);
                    console.log('Top 20 recommendations:', topRecommendations);
                    setRecommendations(topRecommendations);
                    setProgress(100);
                } else {
                    console.log('No recommendations found in response');
                    setError('No recommendations found for this prompt. Please try a different prompt.');
                }
            } else {
                console.log('API returned non-complete status:', response.status);
                // Handle other statuses (like 'pending', 'processing', etc.)
                setError('The service is still processing your request. Please try again in a moment.');
            }
        } catch (err) {
            console.error('Error in handlePromptClick:', err);
            setError('Failed to fetch recommendations. Please check your connection and try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleScoreSaved = (font, score) => {
        setScores(prev => [...prev, { font, score, timestamp: new Date() }]);
    };

    const handleRefresh = () => {
        if (selectedPrompt) {
            handlePromptClick(selectedPrompt);
        }
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900">
            {/* Header */}
            <header className="glass border-b border-white/20">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between items-center py-6">
                        <div className="flex items-center gap-4">
                            <div className="relative">
                                <div className="absolute inset-0 bg-gradient-to-r from-purple-400 to-pink-400 rounded-lg blur opacity-75 animate-pulse-slow"></div>
                                <div className="relative bg-white rounded-lg p-3">
                                    <Sparkles className="text-purple-600" size={28} />
                                </div>
                            </div>
                            <div>
                                <h1 className="text-3xl font-bold text-white mb-1">
                                    Font Recommendation
                                </h1>
                                <p className="text-purple-200 text-sm">AI-Powered Font Evaluation System</p>
                            </div>
                        </div>
                        {selectedPrompt && (
                            <button
                                onClick={handleRefresh}
                                disabled={loading}
                                className="btn-primary flex items-center gap-3 px-6 py-3 rounded-xl text-white font-medium disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                <RefreshCw size={18} className={loading ? 'animate-spin' : ''} />
                                Refresh Results
                            </button>
                        )}
                    </div>
                </div>
            </header>

            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                    {/* Left Column - Prompts Grid */}
                    <div className="lg:col-span-1">
                        <div className="glass rounded-2xl p-6 animate-fade-in-up">
                            <div className="flex items-center gap-3 mb-6">
                                <div className="bg-gradient-to-r from-purple-400 to-pink-400 p-2 rounded-lg">
                                    <TrendingUp className="text-white" size={20} />
                                </div>
                                <h2 className="text-xl font-bold text-white">Font Recommendation Prompts</h2>
                            </div>
                            <PromptGrid
                                onPromptClick={handlePromptClick}
                                selectedPrompt={selectedPrompt}
                            />
                        </div>
                    </div>

                    {/* Right Column - Recommendations and Scoring */}
                    <div className="lg:col-span-2 space-y-6">
                        {error && (
                            <div className="glass border border-red-300/30 rounded-2xl p-6 animate-fade-in-up">
                                <div className="flex items-center gap-3">
                                    <div className="bg-red-500/20 p-2 rounded-lg">
                                        <AlertCircle className="text-red-400" size={20} />
                                    </div>
                                    <div className="flex-1">
                                        <h4 className="text-red-200 font-semibold mb-1">Service Temporarily Unavailable</h4>
                                        <p className="text-red-200 text-sm">{error}</p>
                                    </div>
                                </div>
                                <div className="mt-4 pt-4 border-t border-red-300/20">
                                    <p className="text-red-200 text-xs">
                                        💡 Tip: Try refreshing the page or selecting a different prompt
                                    </p>
                                </div>
                            </div>
                        )}

                        {loading && (
                            <div className="glass rounded-2xl p-8">
                                <LoadingSpinner message={`Fetching font recommendations... ${progress}%`} />
                                {progress > 0 && progress < 100 && (
                                    <div className="mt-6">
                                        <div className="w-full bg-white/20 rounded-full h-3 overflow-hidden">
                                            <div
                                                className="bg-gradient-to-r from-purple-400 to-pink-400 h-3 rounded-full transition-all duration-500"
                                                style={{ width: `${progress}%` }}
                                            ></div>
                                        </div>
                                        <p className="text-purple-200 text-sm mt-3 text-center">
                                            Processing... {progress}% complete
                                        </p>
                                    </div>
                                )}
                            </div>
                        )}

                        {!loading && !error && recommendations.length > 0 && (
                            <>
                                <div className="glass rounded-2xl p-6 animate-fade-in-up">
                                    <FontRecommendations
                                        recommendations={recommendations}
                                        prompt={selectedPrompt}
                                        onScoreSaved={handleScoreSaved}
                                    />
                                </div>

                                {scores.length > 0 && (
                                    <div className="animate-fade-in-up">
                                        <ScoreSummary scores={scores} />
                                    </div>
                                )}
                            </>
                        )}

                        {!loading && !error && selectedPrompt && recommendations.length === 0 && (
                            <div className="glass rounded-2xl p-8 text-center">
                                <div className="text-purple-200">
                                    <div className="relative inline-block mb-6">
                                        <div className="absolute inset-0 bg-gradient-to-r from-purple-400 to-pink-400 rounded-full blur opacity-75 animate-pulse-slow"></div>
                                        <div className="relative bg-white rounded-full p-4">
                                            <Search size={48} className="text-purple-600" />
                                        </div>
                                    </div>
                                    <h3 className="text-xl font-semibold text-white mb-3">
                                        No recommendations found
                                    </h3>
                                    <p className="text-purple-200">
                                        Try selecting a different prompt or refreshing the results.
                                    </p>
                                </div>
                            </div>
                        )}

                        {!selectedPrompt && (
                            <div className="glass rounded-2xl p-8 text-center animate-fade-in-up">
                                <div className="text-purple-200">
                                    <div className="relative inline-block mb-6 animate-float">
                                        <div className="absolute inset-0 bg-gradient-to-r from-purple-400 to-pink-400 rounded-full blur opacity-75 animate-pulse-slow"></div>
                                        <div className="relative bg-white rounded-full p-4">
                                            <Sparkles size={48} className="text-purple-600" />
                                        </div>
                                    </div>
                                    <h3 className="text-2xl font-bold text-white mb-4">
                                        Welcome to Font Recommendation Evaluator
                                    </h3>
                                    <p className="text-purple-200 text-lg mb-6">
                                        Click on any prompt from the grid to fetch font recommendations and start scoring.
                                    </p>
                                    <div className="flex justify-center">
                                        <div className="bg-gradient-to-r from-purple-400/20 to-pink-400/20 rounded-xl p-4 border border-purple-300/30">
                                            <p className="text-purple-200 text-sm">
                                                <span className="font-semibold">20+ Prompts</span> • <span className="font-semibold">AI-Powered</span> • <span className="font-semibold">Real-time Scoring</span>
                                            </p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
