import React from 'react';
import { prompts } from '../data/prompts';

const PromptGrid = ({ onPromptClick, selectedPrompt }) => {
    return (
        <div className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {prompts.map((prompt, index) => (
                    <div
                        key={index}
                        onClick={() => onPromptClick(prompt)}
                        className={`
              group relative overflow-hidden rounded-xl p-4 cursor-pointer transition-all duration-300 transform hover:scale-105
              ${selectedPrompt === prompt
                                ? 'bg-gradient-to-r from-purple-500 to-pink-500 text-white shadow-2xl'
                                : 'bg-white/10 backdrop-blur-sm text-purple-100 hover:bg-white/20 border border-white/20 hover:border-purple-300/50'
                            }
            `}
                    >
                        {/* Background gradient effect */}
                        <div className={`
              absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300
              ${selectedPrompt === prompt
                                ? 'bg-gradient-to-r from-purple-600/20 to-pink-600/20'
                                : 'bg-gradient-to-r from-purple-400/10 to-pink-400/10'
                            }
            `}></div>

                        {/* Content */}
                        <div className="relative z-10">
                            <div className="text-sm font-medium leading-relaxed">
                                {prompt}
                            </div>
                            {selectedPrompt === prompt && (
                                <div className="mt-2 text-xs opacity-90 flex items-center gap-1">
                                    <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
                                    Selected
                                </div>
                            )}
                        </div>

                        {/* Hover effect */}
                        <div className="absolute inset-0 bg-gradient-to-r from-purple-400/0 to-pink-400/0 group-hover:from-purple-400/10 group-hover:to-pink-400/10 transition-all duration-300"></div>
                    </div>
                ))}
            </div>

            {/* Stats section */}
            <div className="mt-6 p-4 bg-white/5 rounded-xl border border-white/10">
                <div className="flex items-center justify-between text-sm text-purple-200">
                    <span>Total Prompts</span>
                    <span className="font-semibold text-white">{prompts.length}</span>
                </div>
                <div className="flex items-center justify-between text-sm text-purple-200 mt-2">
                    <span>Selected</span>
                    <span className="font-semibold text-white">
                        {selectedPrompt ? '1' : '0'}
                    </span>
                </div>
            </div>
        </div>
    );
};

export default PromptGrid;
