import axios from 'axios';
import config, { getApiUrl, getExternalUrl } from '../../config';

// Use config for API endpoint
const API_ENDPOINT = config.API.FONT_RECOMMENDATIONS;

export const fetchFontRecommendations = async (query, prompt = "") => {
    try {
        console.log('Making API request with query:', query);

        const response = await axios.post(API_ENDPOINT, {
            query: query,
            intermediate_query_enabled: true,
            prompt: prompt,
            with_conversion_ranking: "true",
            faiss_optimized: true
        }, {
            headers: {
                'Content-Type': 'application/json',
            },
            timeout: config.current.TIMEOUT
        });

        console.log('API Response received:', response.data);
        console.log('Response status:', response.data.status);
        console.log('Recommendations count:', response.data.results?.recommendations?.length || 0);

        return response.data;
    } catch (error) {
        console.error('Error fetching font recommendations:', error);
        console.error('Error details:', error.response?.data);
        throw error;
    }
};

// Handle streaming Server-Sent Events (SSE) response
export const fetchFontRecommendationsStream = async (query, prompt = "", onProgress) => {
    try {
        const response = await fetch(API_ENDPOINT, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                query: query,
                intermediate_query_enabled: true,
                prompt: prompt,
                with_conversion_ranking: "true",
                faiss_optimized: true
            })
        });

        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }

        const reader = response.body.getReader();
        const decoder = new TextDecoder();
        let buffer = '';

        return new Promise((resolve, reject) => {
            const processStream = async () => {
                try {
                    while (true) {
                        const { done, value } = await reader.read();

                        if (done) {
                            break;
                        }

                        buffer += decoder.decode(value, { stream: true });

                        // Process complete lines from the stream
                        const lines = buffer.split('\n');
                        buffer = lines.pop() || ''; // Keep incomplete line in buffer

                        for (const line of lines) {
                            if (line.trim() && line.startsWith('data: ')) {
                                try {
                                    // Extract JSON from "data: {...}" format
                                    const jsonStr = line.substring(6); // Remove "data: " prefix
                                    const data = JSON.parse(jsonStr);

                                    console.log('Stream data received:', data);

                                    // Call progress callback if provided
                                    if (onProgress) {
                                        onProgress(data);
                                    }

                                    // Check if status is complete
                                    if (data.status === 'complete') {
                                        console.log('Stream completed with data:', data);
                                        resolve(data);
                                        return;
                                    }
                                } catch (parseError) {
                                    console.warn('Failed to parse JSON from stream:', line);
                                }
                            }
                        }
                    }

                    // If we reach here, try to parse the final buffer
                    if (buffer.trim()) {
                        try {
                            const lines = buffer.split('\n');
                            for (const line of lines) {
                                if (line.trim() && line.startsWith('data: ')) {
                                    const jsonStr = line.substring(6);
                                    const finalData = JSON.parse(jsonStr);
                                    if (finalData.status === 'complete') {
                                        resolve(finalData);
                                        return;
                                    }
                                }
                            }
                        } catch (parseError) {
                            reject(new Error('Failed to parse final response'));
                        }
                    }

                    reject(new Error('No complete response received'));
                } catch (error) {
                    reject(error);
                }
            };

            processStream();
        });

    } catch (error) {
        console.error('Error fetching font recommendations stream:', error);
        throw error;
    }
};

// Save font score to database
export const saveFontScore = async (prompt, fontData, score) => {
    try {
        const scoreData = {
            prompt: prompt,
            font_family: fontData.family_name,
            font_style: fontData.style_name,
            font_md5: fontData.md5,
            foundry: fontData.foundry_name,
            score: score, // 'good', 'average', or 'bad'
            timestamp: new Date().toISOString(),
            font_data: fontData, // Complete font object
            user_session: generateSessionId(), // Track user session
            ip_address: await getClientIP(), // Track IP for analytics
            user_agent: navigator.userAgent, // Browser info
            screen_resolution: `${screen.width}x${screen.height}`, // Device info
            timezone: Intl.DateTimeFormat().resolvedOptions().timeZone // User timezone
        };

        console.log('Saving font score to database:', scoreData);

        // Option 1: Save to your own backend API
        const response = await axios.post(config.API.FONT_SCORES, scoreData, {
            headers: {
                'Content-Type': 'application/json',
            },
            timeout: config.current.TIMEOUT
        });

        console.log('Score saved successfully:', response.data);
        return { success: true, data: scoreData, id: response.data.id };

    } catch (error) {
        console.error('Error saving font score:', error);

        // Option 2: Fallback to localStorage if API fails and feature is enabled
        if (config.FEATURES.LOCALSTORAGE_FALLBACK) {
            try {
                const existingScores = JSON.parse(localStorage.getItem('font_scores') || '[]');
                const newScore = {
                    id: Date.now().toString(),
                    prompt: prompt,
                    font_family: fontData.family_name,
                    font_style: fontData.style_name,
                    font_md5: fontData.md5,
                    foundry: fontData.foundry_name,
                    score: score,
                    timestamp: new Date().toISOString(),
                    font_data: fontData
                };

                existingScores.push(newScore);
                localStorage.setItem('font_scores', JSON.stringify(existingScores));

                console.log('Score saved to localStorage as fallback');
                return { success: true, data: newScore, fallback: true };
            } catch (localStorageError) {
                console.error('Failed to save to localStorage:', localStorageError);
                throw error;
            }
        } else {
            throw error;
        }
    }
};

// Get all saved scores (for analytics)
export const getFontScores = async () => {
    try {
        // Try to get from API first
        const response = await axios.get(config.API.FONT_SCORES, {
            timeout: config.current.TIMEOUT
        });
        return response.data;
    } catch (error) {
        console.error('Error fetching scores from API:', error);

        // Fallback to localStorage if feature is enabled
        if (config.FEATURES.LOCALSTORAGE_FALLBACK) {
            try {
                const scores = JSON.parse(localStorage.getItem('font_scores') || '[]');
                return scores;
            } catch (localStorageError) {
                console.error('Error reading from localStorage:', localStorageError);
                return [];
            }
        } else {
            return [];
        }
    }
};

// Generate unique session ID
const generateSessionId = () => {
    return 'session_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
};

// Get client IP (mock implementation)
const getClientIP = async () => {
    if (!config.FEATURES.ANALYTICS_ENABLED) {
        return 'analytics_disabled';
    }

    try {
        const response = await fetch(getExternalUrl(config.API.IP_SERVICE));
        const data = await response.json();
        return data.ip;
    } catch (error) {
        console.error('Error getting IP:', error);
        return 'unknown';
    }
};
