import React, { useState } from 'react';
import { User, Mail, Shield } from 'lucide-react';
import { getUserIdentifier, isCharlesUser, getUserDataPrefix } from '../config';

const UserInfo = () => {
    const [showDetails, setShowDetails] = useState(false);
    const currentUser = getUserIdentifier();
    const isCharles = isCharlesUser();
    const userPrefix = getUserDataPrefix();

    return (
        <div className="fixed bottom-4 right-4 z-50">
            <div className="bg-white rounded-lg shadow-lg border border-gray-200 p-3">
                <button
                    onClick={() => setShowDetails(!showDetails)}
                    className="flex items-center space-x-2 text-sm text-gray-600 hover:text-gray-900 transition-colors"
                >
                    <User className="w-4 h-4" />
                    <span className="font-medium">
                        {isCharles ? 'Charles' : 'User'}
                    </span>
                    <Shield className="w-3 h-3" />
                </button>

                {showDetails && (
                    <div className="mt-2 p-2 bg-gray-50 rounded border text-xs">
                        <div className="flex items-center space-x-1 mb-1">
                            <Mail className="w-3 h-3 text-gray-500" />
                            <span className="text-gray-700">{currentUser}</span>
                        </div>
                        <div className="text-gray-500">
                            Data Prefix: {userPrefix}
                        </div>
                        {isCharles && (
                            <div className="mt-1 text-blue-600 font-medium">
                                Charles's Data (Segregated)
                            </div>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
};

export default UserInfo;
