import React from 'react';
import { prompts } from '../data/prompts';

const PromptGrid = ({ onPromptClick, selectedPrompt }) => {
    return (
        <div className="space-y-2 h-full overflow-y-auto">
            {prompts.map((prompt, index) => (
                <button
                    key={index}
                    onClick={() => onPromptClick(prompt)}
                    className={`w-full text-left p-3 sm:p-4 rounded-xl transition-all duration-200 ${selectedPrompt === prompt
                        ? 'bg-blue-50 border-2 border-blue-200 shadow-sm'
                        : 'bg-white hover:bg-gray-50 border border-gray-200'
                        }`}
                >
                    <div className="flex items-start justify-between">
                        <div className="flex-1 min-w-0">
                            <h3 className={`font-medium text-xs sm:text-sm mb-1 ${selectedPrompt === prompt ? 'text-blue-700' : 'text-gray-900'
                                } truncate`}>
                                {prompt}
                            </h3>
                            <p className="text-gray-500 text-xs">
                                Click to analyze fonts
                            </p>
                        </div>

                        {selectedPrompt === prompt && (
                            <div className="ml-2 sm:ml-3 flex-shrink-0">
                                <div className="w-2 h-2 bg-blue-500 rounded-full animate-pulse"></div>
                            </div>
                        )}
                    </div>
                </button>
            ))}
        </div>
    );
};

export default PromptGrid;
