import React from 'react';
import { CheckCircle, AlertCircle, XCircle } from 'lucide-react';

const ScoreSummary = ({ scores }) => {
    const goodScores = scores.filter(s => s.score === 'good').length;
    const averageScores = scores.filter(s => s.score === 'average').length;
    const badScores = scores.filter(s => s.score === 'bad').length;
    const totalScores = scores.length;

    const goodPercentage = totalScores > 0 ? Math.round((goodScores / totalScores) * 100) : 0;
    const averagePercentage = totalScores > 0 ? Math.round((averageScores / totalScores) * 100) : 0;
    const badPercentage = totalScores > 0 ? Math.round((badScores / totalScores) * 100) : 0;

    return (
        <div className="bg-white rounded-lg p-6 shadow-sm">
            <div className="space-y-4">
                {/* Overall Progress */}
                <div>
                    <h3 className="font-bold text-black mb-2">Overall Progress:</h3>
                    <p className="text-gray-600">{totalScores}/21 results evaluated ({Math.round((totalScores / 21) * 100)}%)</p>
                </div>

                {/* Score Distribution */}
                <div>
                    <h3 className="font-bold text-black mb-3">Score Distribution:</h3>
                    <div className="flex space-x-3">
                        <span className="bg-green-600 text-white px-3 py-1 rounded-full text-sm font-medium flex items-center">
                            <CheckCircle size={16} className="mr-1" />
                            {goodPercentage}%
                        </span>
                        <span className="bg-orange-500 text-white px-3 py-1 rounded-full text-sm font-medium flex items-center">
                            <AlertCircle size={16} className="mr-1" />
                            {averagePercentage}%
                        </span>
                        <span className="bg-red-500 text-white px-3 py-1 rounded-full text-sm font-medium flex items-center">
                            <XCircle size={16} className="mr-1" />
                            {badPercentage}%
                        </span>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ScoreSummary;
