import React, { useState } from 'react';
import { Search, RefreshCw, Sparkles, TrendingUp, Award, AlertCircle, Zap, Users, BarChart3 } from 'lucide-react';
import PromptGrid from './components/PromptGrid';
import FontRecommendations from './components/FontRecommendations';
import LoadingSpinner from './components/LoadingSpinner';
import ScoreSummary from './components/ScoreSummary';
import { fetchFontRecommendationsStream } from './services/api';
import config from './config';

function App() {
    const [selectedPrompt, setSelectedPrompt] = useState('');
    const [recommendations, setRecommendations] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [scores, setScores] = useState([]);
    const [progress, setProgress] = useState(0);

    const handlePromptClick = async (prompt) => {
        setSelectedPrompt(prompt);
        setLoading(true);
        setError('');
        setRecommendations([]);
        setScores([]);
        setProgress(0);

        try {
            const response = await fetchFontRecommendationsStream(
                prompt,
                "",
                (data) => {
                    // Handle progress updates
                    if (data.progress !== undefined) {
                        setProgress(data.progress);
                    }

                    // If we have partial results, show them
                    if (data.results?.recommendations) {
                        setRecommendations(data.results.recommendations.slice(0, config.UI.MAX_RECOMMENDATIONS));
                    }
                }
            );

            console.log('Final API Response:', response);
            console.log('Response status:', response.status);
            console.log('Response results:', response.results);
            console.log('Recommendations count:', response.results?.recommendations?.length || 0);

            // Check for API error status
            if (response.status === 'error') {
                console.log('API returned error status');
                setError('Something went wrong with the font recommendation service. Please try again later.');
                return;
            }

            // Check if status is complete and we have recommendations
            if (response.status === 'complete') {
                console.log('API returned complete status');
                if (response.results && response.results.recommendations && response.results.recommendations.length > 0) {
                    console.log('Found recommendations, processing...');
                    const topRecommendations = response.results.recommendations.slice(0, config.UI.MAX_RECOMMENDATIONS);
                    console.log(`Top ${config.UI.MAX_RECOMMENDATIONS} recommendations:`, topRecommendations);
                    setRecommendations(topRecommendations);
                    setProgress(100);
                } else {
                    console.log('No recommendations found in response');
                    setError('No recommendations found for this prompt. Please try a different prompt.');
                }
            } else {
                console.log('API returned non-complete status:', response.status);
                // Handle other statuses (like 'pending', 'processing', etc.)
                setError('The service is still processing your request. Please try again in a moment.');
            }
        } catch (err) {
            console.error('Error in handlePromptClick:', err);
            setError('Failed to fetch recommendations. Please check your connection and try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleScoreSaved = (font, score) => {
        setScores(prev => [...prev, { font, score, timestamp: new Date() }]);
    };

    const handleRefresh = () => {
        if (selectedPrompt) {
            handlePromptClick(selectedPrompt);
        }
    };

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Simple Header */}
            <header className="bg-white border-b border-gray-200">
                <div className="max-w-7xl mx-auto px-6 py-4">
                    <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-4">
                            <div className="bg-blue-500 rounded-xl p-3">
                                <Zap className="text-white" size={24} />
                            </div>
                            <div>
                                <h1 className="text-2xl font-bold text-gray-900">
                                    Expert & AI Eval calibrator
                                </h1>
                                <p className="text-gray-600 text-sm">Evaluate prompts and their results</p>
                            </div>
                        </div>

                        {selectedPrompt && (
                            <button
                                onClick={handleRefresh}
                                disabled={loading}
                                className="btn-primary flex items-center space-x-2 px-4 py-2 rounded-xl text-sm font-medium disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                <RefreshCw size={16} className={loading ? 'animate-spin' : ''} />
                                <span>Refresh</span>
                            </button>
                        )}
                    </div>
                </div>
            </header>

            <div className="max-w-7xl mx-auto px-6 py-8">
                <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
                    {/* Sidebar - Prompts */}
                    <div className="lg:col-span-1">
                        <div className="card-simple p-6 animate-slide-in-left">
                            <div className="flex items-center space-x-3 mb-6">
                                <div className="bg-blue-500 p-2 rounded-lg">
                                    <TrendingUp className="text-white" size={18} />
                                </div>
                                <h2 className="text-lg font-semibold text-gray-900">Prompts</h2>
                            </div>
                            <PromptGrid
                                onPromptClick={handlePromptClick}
                                selectedPrompt={selectedPrompt}
                            />
                        </div>
                    </div>

                    {/* Main Content */}
                    <div className="lg:col-span-3 space-y-6">
                        {/* Score Summary at Top */}
                        <div className="animate-fade-in-up">
                            <ScoreSummary scores={scores} totalResults={recommendations.length} />
                        </div>

                        {/* Error State */}
                        {error && (
                            <div className="card-simple p-6 animate-fade-in-up">
                                <div className="flex items-start space-x-4">
                                    <div className="bg-red-100 p-2 rounded-lg">
                                        <AlertCircle className="text-red-600" size={20} />
                                    </div>
                                    <div className="flex-1">
                                        <h4 className="text-red-900 font-semibold mb-2">Service Unavailable</h4>
                                        <p className="text-red-700 text-sm mb-3">{error}</p>
                                        <div className="flex items-center space-x-2 text-xs text-red-600">
                                            <span>💡</span>
                                            <span>Try refreshing or selecting a different prompt</span>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Loading State */}
                        {loading && (
                            <div className="card-simple p-8 animate-fade-in-up">
                                <LoadingSpinner message={`Analyzing fonts... ${progress}%`} />
                                {progress > 0 && progress < 100 && (
                                    <div className="mt-6">
                                        <div className="progress-modern">
                                            <div
                                                className="progress-modern-fill"
                                                style={{ width: `${progress}%` }}
                                            ></div>
                                        </div>
                                        <p className="text-gray-600 text-sm mt-3 text-center">
                                            Processing recommendations... {progress}% complete
                                        </p>
                                    </div>
                                )}
                            </div>
                        )}

                        {/* Results */}
                        {!loading && !error && recommendations.length > 0 && (
                            <div className="card-simple p-6 animate-fade-in-up">
                                <FontRecommendations
                                    recommendations={recommendations}
                                    prompt={selectedPrompt}
                                    onScoreSaved={handleScoreSaved}
                                />
                            </div>
                        )}

                        {/* No Results */}
                        {!loading && !error && selectedPrompt && recommendations.length === 0 && (
                            <div className="card-simple p-8 text-center animate-fade-in-up">
                                <div className="text-gray-600">
                                    <div className="bg-blue-100 rounded-full p-4 mb-6 inline-block">
                                        <Search size={40} className="text-blue-600" />
                                    </div>
                                    <h3 className="text-lg font-semibold text-gray-900 mb-3">
                                        No recommendations found
                                    </h3>
                                    <p className="text-gray-600">
                                        Try selecting a different prompt or refreshing the results.
                                    </p>
                                </div>
                            </div>
                        )}

                        {/* Welcome State */}
                        {!selectedPrompt && (
                            <div className="card-simple p-8 text-center animate-fade-in-up">
                                <div className="text-gray-600">
                                    <div className="bg-blue-100 rounded-full p-4 mb-6 inline-block">
                                        <Sparkles size={40} className="text-blue-600" />
                                    </div>
                                    <h3 className="text-2xl font-bold text-gray-900 mb-4">
                                        Welcome to Font Evaluator
                                    </h3>
                                    <p className="text-gray-600 text-lg mb-8">
                                        Select a prompt to analyze font recommendations and provide feedback.
                                    </p>

                                    {/* Feature Cards */}
                                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
                                        <div className="bg-gray-50 rounded-xl p-4 border border-gray-200">
                                            <div className="flex items-center justify-center mb-3">
                                                <Zap className="text-blue-500" size={20} />
                                            </div>
                                            <h4 className="text-gray-900 font-semibold text-sm mb-1">AI-Powered</h4>
                                            <p className="text-gray-500 text-xs">Advanced font analysis</p>
                                        </div>

                                        <div className="bg-gray-50 rounded-xl p-4 border border-gray-200">
                                            <div className="flex items-center justify-center mb-3">
                                                <Users className="text-blue-500" size={20} />
                                            </div>
                                            <h4 className="text-gray-900 font-semibold text-sm mb-1">20+ Prompts</h4>
                                            <p className="text-gray-500 text-xs">Diverse use cases</p>
                                        </div>

                                        <div className="bg-gray-50 rounded-xl p-4 border border-gray-200">
                                            <div className="flex items-center justify-center mb-3">
                                                <BarChart3 className="text-blue-500" size={20} />
                                            </div>
                                            <h4 className="text-gray-900 font-semibold text-sm mb-1">Real-time Scoring</h4>
                                            <p className="text-gray-500 text-xs">Instant feedback</p>
                                        </div>
                                    </div>

                                    <div className="bg-blue-50 rounded-xl p-4 border border-blue-200">
                                        <p className="text-blue-900 text-sm">
                                            <span className="font-semibold">Click any prompt</span> to get started with font evaluation
                                        </p>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
