import React, { useState } from 'react';
import { ThumbsUp, Minus, ThumbsDown, Award, Tag, Building } from 'lucide-react';
import { saveFontScore } from '../services/api';

const FontRecommendations = ({ recommendations, prompt, onScoreSaved }) => {
    const [scoredFonts, setScoredFonts] = useState(new Set());
    const [savingStates, setSavingStates] = useState({});

    const handleScore = async (font, score) => {
        if (scoredFonts.has(font.md5)) return;

        setSavingStates(prev => ({ ...prev, [font.md5]: true }));

        try {
            await saveFontScore(prompt, font, score);
            setScoredFonts(prev => new Set([...prev, font.md5]));
            onScoreSaved(font, score);
        } catch (error) {
            console.error('Error saving score:', error);
        } finally {
            setSavingStates(prev => ({ ...prev, [font.md5]: false }));
        }
    };

    const getScoreButton = (font, score, icon, label, colorClass) => (
        <button
            onClick={() => handleScore(font, score)}
            disabled={scoredFonts.has(font.md5) || savingStates[font.md5]}
            className={`flex items-center space-x-2 px-4 py-2 rounded-lg text-sm font-medium transition-all duration-200 ${scoredFonts.has(font.md5)
                    ? 'opacity-50 cursor-not-allowed'
                    : `hover:scale-105 ${colorClass}`
                }`}
        >
            {savingStates[font.md5] ? (
                <div className="loading-dots">
                    <span></span>
                    <span></span>
                    <span></span>
                </div>
            ) : (
                <>
                    {icon}
                    <span>{label}</span>
                </>
            )}
        </button>
    );

    return (
        <div className="space-y-6">
            {/* Header */}
            <div className="flex items-center justify-between">
                <div>
                    <h2 className="text-xl font-bold text-white mb-2">Font Recommendations</h2>
                    <p className="text-slate-300 text-sm">
                        {recommendations.length} fonts found for "{prompt}"
                    </p>
                </div>
                <div className="flex items-center space-x-2">
                    <div className="badge-modern badge-success">
                        {recommendations.length} results
                    </div>
                </div>
            </div>

            {/* Font Cards */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {recommendations.map((font, index) => (
                    <div
                        key={font.md5}
                        className={`card-modern p-6 transition-all duration-300 ${scoredFonts.has(font.md5) ? 'ring-2 ring-green-400/50' : ''
                            }`}
                    >
                        {/* Header with ranking */}
                        <div className="flex items-start justify-between mb-4">
                            <div className="flex-1">
                                <div className="flex items-center space-x-2 mb-2">
                                    {index < 3 && (
                                        <div className="flex items-center justify-center w-6 h-6 bg-gradient-to-r from-yellow-400 to-orange-400 rounded-full">
                                            <Award className="text-white" size={12} />
                                        </div>
                                    )}
                                    <h3 className="font-semibold text-gray-900 text-lg">
                                        {font.family_name}
                                    </h3>
                                </div>
                                <p className="text-gray-600 text-sm mb-2">{font.style_name}</p>
                                <div className="flex items-center space-x-2 text-xs text-gray-500">
                                    <Building className="w-3 h-3" />
                                    <span>{font.foundry_name}</span>
                                </div>
                            </div>

                            <div className="text-right">
                                <div className="text-2xl font-bold text-blue-600">
                                    {font.score.toFixed(1)}
                                </div>
                                <div className="text-xs text-gray-500">Score</div>
                            </div>
                        </div>

                        {/* Font details */}
                        <div className="space-y-3 mb-4">
                            {/* Classification */}
                            <div>
                                <div className="flex items-center space-x-2 mb-2">
                                    <Tag className="w-4 h-4 text-gray-500" />
                                    <span className="text-xs font-medium text-gray-700">Classification</span>
                                </div>
                                <div className="flex flex-wrap gap-1">
                                    {font.classification?.slice(0, 3).map((cat, idx) => (
                                        <span
                                            key={idx}
                                            className="px-2 py-1 bg-blue-100 text-blue-700 text-xs rounded-full"
                                        >
                                            {cat}
                                        </span>
                                    ))}
                                </div>
                            </div>

                            {/* Visual Properties */}
                            <div>
                                <div className="flex items-center space-x-2 mb-2">
                                    <div className="w-4 h-4 bg-gradient-to-r from-purple-400 to-pink-400 rounded"></div>
                                    <span className="text-xs font-medium text-gray-700">Properties</span>
                                </div>
                                <div className="flex flex-wrap gap-1">
                                    {font.visualProperty?.slice(0, 3).map((prop, idx) => (
                                        <span
                                            key={idx}
                                            className="px-2 py-1 bg-purple-100 text-purple-700 text-xs rounded-full"
                                        >
                                            {prop}
                                        </span>
                                    ))}
                                </div>
                            </div>

                            {/* Tags */}
                            {font.advanced_tags && font.advanced_tags.length > 0 && (
                                <div>
                                    <span className="text-xs font-medium text-gray-700 mb-2 block">Tags</span>
                                    <div className="flex flex-wrap gap-1">
                                        {font.advanced_tags.slice(0, 4).map((tag, idx) => (
                                            <span
                                                key={idx}
                                                className="px-2 py-1 bg-gray-100 text-gray-600 text-xs rounded-full"
                                            >
                                                {tag}
                                            </span>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* Scoring buttons */}
                        <div className="flex items-center justify-between pt-4 border-t border-gray-200">
                            {getScoreButton(
                                font,
                                'good',
                                <ThumbsUp className="w-4 h-4" />,
                                'Good Match',
                                'bg-green-500 hover:bg-green-600 text-white'
                            )}

                            {getScoreButton(
                                font,
                                'average',
                                <Minus className="w-4 h-4" />,
                                'Average',
                                'bg-yellow-500 hover:bg-yellow-600 text-white'
                            )}

                            {getScoreButton(
                                font,
                                'bad',
                                <ThumbsDown className="w-4 h-4" />,
                                'Bad Match',
                                'bg-red-500 hover:bg-red-600 text-white'
                            )}
                        </div>

                        {/* Scored indicator */}
                        {scoredFonts.has(font.md5) && (
                            <div className="mt-3 flex items-center justify-center">
                                <div className="badge-modern badge-success">
                                    ✓ Scored
                                </div>
                            </div>
                        )}
                    </div>
                ))}
            </div>

            {/* Footer */}
            <div className="text-center pt-6 border-t border-gray-200">
                <p className="text-gray-500 text-sm">
                    Rate each font based on how well it matches the prompt
                </p>
            </div>
        </div>
    );
};

export default FontRecommendations;
