import React from 'react';
import { prompts } from '../data/prompts';

const PromptGrid = ({ onPromptClick, selectedPrompt }) => {
    return (
        <div className="space-y-3">
            <div className="flex items-center justify-between mb-4">
                <span className="text-gray-600 text-sm font-medium">
                    {prompts.length} prompts available
                </span>
                <div className="badge-simple badge-success">
                    Active
                </div>
            </div>

            <div className="space-y-2 max-h-96 overflow-y-auto">
                {prompts.map((prompt, index) => (
                    <button
                        key={index}
                        onClick={() => onPromptClick(prompt)}
                        className={`w-full text-left p-4 rounded-xl transition-all duration-200 ${selectedPrompt === prompt
                            ? 'bg-blue-50 border-2 border-blue-200 shadow-sm'
                            : 'bg-white hover:bg-gray-50 border border-gray-200'
                            }`}
                    >
                        <div className="flex items-start justify-between">
                            <div className="flex-1 min-w-0">
                                <h3 className={`font-medium text-sm mb-1 ${selectedPrompt === prompt ? 'text-blue-700' : 'text-gray-900'
                                    }`}>
                                    {prompt}
                                </h3>
                                <p className="text-gray-500 text-xs">
                                    Click to analyze fonts
                                </p>
                            </div>

                            {selectedPrompt === prompt && (
                                <div className="ml-3 flex-shrink-0">
                                    <div className="w-2 h-2 bg-blue-500 rounded-full animate-pulse"></div>
                                </div>
                            )}
                        </div>
                    </button>
                ))}
            </div>

            <div className="pt-4 border-t border-gray-200">
                <div className="flex items-center justify-center space-x-2 text-xs text-gray-500">
                    <div className="w-1 h-1 bg-green-500 rounded-full"></div>
                    <span>Ready to evaluate</span>
                </div>
            </div>
        </div>
    );
};

export default PromptGrid;
