import React from 'react';
import { BarChart3, CheckCircle, XCircle, MinusCircle } from 'lucide-react';

const ScoreSummary = ({ scores }) => {
    const totalScored = scores.length;
    const goodMatches = scores.filter(s => s.score === 'good').length;
    const averageMatches = scores.filter(s => s.score === 'average').length;
    const badMatches = scores.filter(s => s.score === 'bad').length;

    const getPercentage = (count) => {
        return totalScored > 0 ? ((count / totalScored) * 100).toFixed(1) : 0;
    };

    return (
        <div className="bg-white rounded-lg p-6 shadow-sm border border-gray-200">
            <div className="flex items-center gap-2 mb-4">
                <BarChart3 className="text-primary-500" size={20} />
                <h3 className="text-lg font-semibold text-gray-800">Scoring Summary</h3>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="flex items-center gap-3 p-3 bg-green-50 rounded-lg">
                    <CheckCircle className="text-green-600" size={20} />
                    <div>
                        <div className="text-sm font-medium text-green-800">Good Matches</div>
                        <div className="text-lg font-bold text-green-900">{goodMatches}</div>
                        <div className="text-xs text-green-600">{getPercentage(goodMatches)}%</div>
                    </div>
                </div>

                <div className="flex items-center gap-3 p-3 bg-yellow-50 rounded-lg">
                    <MinusCircle className="text-yellow-600" size={20} />
                    <div>
                        <div className="text-sm font-medium text-yellow-800">Average Matches</div>
                        <div className="text-lg font-bold text-yellow-900">{averageMatches}</div>
                        <div className="text-xs text-yellow-600">{getPercentage(averageMatches)}%</div>
                    </div>
                </div>

                <div className="flex items-center gap-3 p-3 bg-red-50 rounded-lg">
                    <XCircle className="text-red-600" size={20} />
                    <div>
                        <div className="text-sm font-medium text-red-800">Bad Matches</div>
                        <div className="text-lg font-bold text-red-900">{badMatches}</div>
                        <div className="text-xs text-red-600">{getPercentage(badMatches)}%</div>
                    </div>
                </div>
            </div>

            <div className="mt-4 pt-4 border-t border-gray-200">
                <div className="text-sm text-gray-600">
                    Total Fonts Scored: <span className="font-semibold text-gray-800">{totalScored}</span>
                </div>
            </div>
        </div>
    );
};

export default ScoreSummary;
