import React, { useState } from 'react';
import { CheckCircle, XCircle, MinusCircle, Star, TrendingUp, Eye } from 'lucide-react';
import { saveFontScore } from '../services/api';

const FontRecommendations = ({ recommendations, prompt, onScoreSaved }) => {
    const [scoredFonts, setScoredFonts] = useState(new Set());
    const [loading, setLoading] = useState({});

    const handleScore = async (font, score) => {
        if (scoredFonts.has(font.md5)) return;

        setLoading(prev => ({ ...prev, [font.md5]: true }));

        try {
            await saveFontScore(prompt, font, score);
            setScoredFonts(prev => new Set([...prev, font.md5]));
            onScoreSaved && onScoreSaved(font, score);
        } catch (error) {
            console.error('Error saving score:', error);
        } finally {
            setLoading(prev => ({ ...prev, [font.md5]: false }));
        }
    };

    const getScoreButton = (font, score, icon, color, label) => (
        <button
            onClick={() => handleScore(font, score)}
            disabled={scoredFonts.has(font.md5) || loading[font.md5]}
            className={`
        flex items-center gap-2 px-4 py-2 rounded-lg text-sm font-medium transition-all duration-300 transform hover:scale-105
        ${scoredFonts.has(font.md5)
                    ? 'bg-white/10 text-purple-200 cursor-not-allowed'
                    : `${color} hover:shadow-lg`
                }
      `}
        >
            {icon}
            {label}
            {loading[font.md5] && (
                <div className="w-4 h-4 border-2 border-current border-t-transparent rounded-full animate-spin"></div>
            )}
        </button>
    );

    return (
        <div className="space-y-6">
            <div className="flex items-center gap-3 mb-6">
                <div className="bg-gradient-to-r from-purple-400 to-pink-400 p-2 rounded-lg">
                    <Eye className="text-white" size={20} />
                </div>
                <div>
                    <h3 className="text-xl font-semibold text-white">
                        Recommendations for: "{prompt}"
                    </h3>
                    <p className="text-purple-200 text-sm">
                        {recommendations.length} fonts found • Click to score each recommendation
                    </p>
                </div>
            </div>

            <div className="space-y-4">
                {recommendations.map((font, index) => (
                    <div
                        key={font.md5}
                        className={`
              group relative overflow-hidden rounded-xl p-6 transition-all duration-300 card-hover
              ${scoredFonts.has(font.md5)
                                ? 'bg-gradient-to-r from-green-500/20 to-emerald-500/20 border border-green-300/30'
                                : 'bg-white/10 backdrop-blur-sm border border-white/20 hover:border-purple-300/50'
                            }
            `}
                    >
                        {/* Background gradient effect */}
                        <div className={`
              absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300
              ${scoredFonts.has(font.md5)
                                ? 'bg-gradient-to-r from-green-400/10 to-emerald-400/10'
                                : 'bg-gradient-to-r from-purple-400/10 to-pink-400/10'
                            }
            `}></div>

                        <div className="relative z-10">
                            {/* Header */}
                            <div className="flex justify-between items-start mb-4">
                                <div className="flex-1">
                                    <div className="flex items-center gap-3 mb-2">
                                        <h4 className="text-xl font-bold text-white">
                                            {font.family_name}
                                        </h4>
                                        {index < 3 && (
                                            <div className="bg-gradient-to-r from-yellow-400 to-orange-400 px-2 py-1 rounded-full text-xs font-bold text-white">
                                                Top {index + 1}
                                            </div>
                                        )}
                                    </div>
                                    <p className="text-purple-200 text-sm">{font.style_name}</p>
                                    <p className="text-purple-300 text-xs">Foundry: {font.foundry_name}</p>
                                </div>
                                <div className="text-right">
                                    <div className="flex items-center gap-2 mb-2">
                                        <Star className="text-yellow-400" size={16} />
                                        <span className="text-lg font-bold text-white">
                                            {font.score.toFixed(1)}
                                        </span>
                                    </div>
                                    <div className="flex items-center gap-2">
                                        <TrendingUp className="text-green-400" size={16} />
                                        <span className="text-sm text-purple-200">
                                            {font.conversion.toLocaleString()}
                                        </span>
                                    </div>
                                </div>
                            </div>

                            {/* Tags */}
                            <div className="mb-4">
                                <div className="flex flex-wrap gap-2 mb-3">
                                    {font.classification.map((tag, idx) => (
                                        <span
                                            key={idx}
                                            className="px-3 py-1 bg-blue-500/20 text-blue-200 text-xs rounded-full border border-blue-300/30"
                                        >
                                            {tag}
                                        </span>
                                    ))}
                                </div>
                                <div className="flex flex-wrap gap-2">
                                    {font.visualProperty.map((prop, idx) => (
                                        <span
                                            key={idx}
                                            className="px-3 py-1 bg-purple-500/20 text-purple-200 text-xs rounded-full border border-purple-300/30"
                                        >
                                            {prop}
                                        </span>
                                    ))}
                                </div>
                            </div>

                            {/* Advanced Tags */}
                            {font.advanced_tags && font.advanced_tags.length > 0 && (
                                <div className="mb-4">
                                    <div className="text-xs text-purple-300 mb-2 font-medium">Advanced Tags:</div>
                                    <div className="flex flex-wrap gap-1">
                                        {font.advanced_tags.map((tag, idx) => (
                                            <span
                                                key={idx}
                                                className="px-2 py-1 bg-green-500/20 text-green-200 text-xs rounded-full border border-green-300/30"
                                            >
                                                {tag}
                                            </span>
                                        ))}
                                    </div>
                                </div>
                            )}

                            {/* Scoring Section */}
                            {scoredFonts.has(font.md5) ? (
                                <div className="flex items-center gap-2 text-green-400">
                                    <CheckCircle size={18} />
                                    <span className="text-sm font-medium">Scored Successfully</span>
                                </div>
                            ) : (
                                <div className="flex gap-3">
                                    {getScoreButton(
                                        font,
                                        'good',
                                        <CheckCircle size={16} />,
                                        'bg-gradient-to-r from-green-500 to-emerald-500 text-white',
                                        'Good Match'
                                    )}
                                    {getScoreButton(
                                        font,
                                        'average',
                                        <MinusCircle size={16} />,
                                        'bg-gradient-to-r from-yellow-500 to-orange-500 text-white',
                                        'Average Match'
                                    )}
                                    {getScoreButton(
                                        font,
                                        'bad',
                                        <XCircle size={16} />,
                                        'bg-gradient-to-r from-red-500 to-pink-500 text-white',
                                        'Bad Match'
                                    )}
                                </div>
                            )}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default FontRecommendations;
