import axios from 'axios';

const API_BASE_URL = 'https://fontgpt-backend.monotype-pp.com';

export const fetchFontRecommendations = async (query, prompt = "") => {
    try {
        console.log('Making API request with query:', query);

        const response = await axios.post(`${API_BASE_URL}/fontrecommendations/typesense/stream`, {
            query: query,
            intermediate_query_enabled: true,
            prompt: prompt,
            with_conversion_ranking: "true",
            faiss_optimized: true
        }, {
            headers: {
                'Content-Type': 'application/json',
            },
        });

        console.log('API Response received:', response.data);
        console.log('Response status:', response.data.status);
        console.log('Recommendations count:', response.data.results?.recommendations?.length || 0);

        return response.data;
    } catch (error) {
        console.error('Error fetching font recommendations:', error);
        console.error('Error details:', error.response?.data);
        throw error;
    }
};

// Alternative implementation for true streaming (if the API supports SSE)
export const fetchFontRecommendationsStream = async (query, prompt = "", onProgress) => {
    try {
        const response = await fetch(`${API_BASE_URL}/fontrecommendations/typesense/stream`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                query: query,
                intermediate_query_enabled: true,
                prompt: prompt,
                with_conversion_ranking: "true",
                faiss_optimized: true
            })
        });

        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }

        // For now, let's try to get the response as JSON directly
        const data = await response.json();

        // Call progress callback if provided
        if (onProgress) {
            onProgress(data);
        }

        return data;

    } catch (error) {
        console.error('Error fetching font recommendations stream:', error);
        throw error;
    }
};

// Mock function for storing scores in database
export const saveFontScore = async (prompt, fontData, score) => {
    try {
        // This would be replaced with actual database call
        const scoreData = {
            prompt: prompt,
            font_family: fontData.family_name,
            font_style: fontData.style_name,
            font_md5: fontData.md5,
            foundry: fontData.foundry_name,
            score: score,
            timestamp: new Date().toISOString(),
            font_data: fontData
        };

        console.log('Saving font score:', scoreData);

        // Simulate API call delay
        await new Promise(resolve => setTimeout(resolve, 500));

        return { success: true, data: scoreData };
    } catch (error) {
        console.error('Error saving font score:', error);
        throw error;
    }
};
