import React, { useState, useEffect } from 'react';
import { Zap, TrendingUp, RefreshCw, AlertCircle, Menu, X } from 'lucide-react';
import PromptGrid from './components/PromptGrid';
import FontRecommendations from './components/FontRecommendations';
import LoadingSpinner from './components/LoadingSpinner';
import ScoreSummary from './components/ScoreSummary';
import { fetchFontRecommendationsStream } from './services/api';
import config from './config';

function App() {
    const [selectedPrompt, setSelectedPrompt] = useState('');
    const [recommendations, setRecommendations] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [progress, setProgress] = useState(0);
    const [scores, setScores] = useState([]);
    const [mobileNavOpen, setMobileNavOpen] = useState(false);

    const handlePromptClick = async (prompt) => {
        setSelectedPrompt(prompt);
        setLoading(true);
        setError('');
        setProgress(0);
        setScores([]);

        try {
            const data = await fetchFontRecommendationsStream(prompt, prompt, (progressData) => {
                if (progressData.progress) {
                    setProgress(progressData.progress);
                }
            });

            if (data.results && data.results.recommendations) {
                setRecommendations(data.results.recommendations.slice(0, config.UI.MAX_RECOMMENDATIONS));
            } else {
                setError('No recommendations found');
            }
        } catch (err) {
            console.error('Error fetching recommendations:', err);
            setError(err.message || 'Failed to fetch recommendations');
        } finally {
            setLoading(false);
            setProgress(0);
        }
    };

    const handleScoreSaved = (font, score) => {
        setScores(prev => [...prev, { font, score, timestamp: new Date() }]);
    };

    const handleRefresh = () => {
        if (selectedPrompt) {
            handlePromptClick(selectedPrompt);
        }
    };

    const toggleMobileNav = () => {
        setMobileNavOpen(!mobileNavOpen);
    };

    const closeMobileNav = () => {
        setMobileNavOpen(false);
    };

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Simple Header */}
            <header className="bg-white border-b border-gray-200">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 py-3 sm:py-4">
                    <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-3 sm:space-y-0">
                        <div className="flex items-center justify-between">
                            <div className="flex items-center space-x-3 sm:space-x-4">
                                <div className="bg-blue-500 rounded-xl p-2 sm:p-3">
                                    <Zap className="text-white sm:w-6 sm:h-6" size={20} />
                                </div>
                                <div className="min-w-0 flex-1">
                                    <h1 className="text-lg sm:text-2xl font-bold text-gray-900 truncate">
                                        Expert & AI Eval calibrator
                                    </h1>
                                    <p className="text-gray-600 text-xs sm:text-sm">Evaluate prompts and their results</p>
                                </div>
                            </div>

                            {/* Mobile Menu Button */}
                            <button
                                onClick={toggleMobileNav}
                                className="lg:hidden p-2 rounded-lg hover:bg-gray-100 transition-colors"
                                aria-label="Toggle navigation menu"
                            >
                                <Menu className="w-6 h-6 text-gray-600" />
                            </button>
                        </div>

                        {selectedPrompt && (
                            <button
                                onClick={handleRefresh}
                                disabled={loading}
                                className="btn-primary flex items-center justify-center sm:justify-start space-x-2 px-3 sm:px-4 py-2 rounded-xl text-xs sm:text-sm font-medium disabled:opacity-50 disabled:cursor-not-allowed w-full sm:w-auto"
                            >
                                <RefreshCw size={14} className={`${loading ? 'animate-spin' : ''} sm:w-4 sm:h-4`} />
                                <span>Refresh</span>
                            </button>
                        )}
                    </div>
                </div>
            </header>

            {/* Mobile Navigation Overlay */}
            {mobileNavOpen && (
                <div
                    className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden"
                    onClick={closeMobileNav}
                />
            )}

            {/* Mobile Side Navigation */}
            <div className={`fixed top-0 left-0 h-full w-80 bg-white shadow-xl transform transition-transform duration-300 ease-in-out z-50 lg:hidden ${mobileNavOpen ? 'translate-x-0' : '-translate-x-full'
                }`}>
                <div className="flex items-center justify-between p-4 border-b border-gray-200">
                    <h2 className="text-lg font-semibold text-gray-900">Prompts</h2>
                    <button
                        onClick={closeMobileNav}
                        className="p-2 rounded-lg hover:bg-gray-100 transition-colors"
                        aria-label="Close navigation"
                    >
                        <X className="w-6 h-6 text-gray-600" />
                    </button>
                </div>
                <div className="p-4">
                    <PromptGrid
                        onPromptClick={(prompt) => {
                            handlePromptClick(prompt);
                            closeMobileNav();
                        }}
                        selectedPrompt={selectedPrompt}
                    />
                </div>
            </div>

            <div className="max-w-7xl mx-auto px-4 sm:px-6 py-4 sm:py-8">
                <div className="grid grid-cols-1 lg:grid-cols-4 gap-4 sm:gap-8">
                    {/* Desktop Sidebar - Prompts */}
                    <div className="hidden lg:block lg:col-span-1 order-2 lg:order-1">
                        <div className="card-simple p-4 sm:p-6 animate-slide-in-left">
                            <div className="flex items-center space-x-3 mb-4 sm:mb-6">
                                <div className="bg-blue-500 p-2 rounded-lg">
                                    <TrendingUp className="text-white sm:w-5 sm:h-5" size={16} />
                                </div>
                                <h2 className="text-base sm:text-lg font-semibold text-gray-900">Prompts</h2>
                            </div>
                            <PromptGrid
                                onPromptClick={handlePromptClick}
                                selectedPrompt={selectedPrompt}
                            />
                        </div>
                    </div>

                    {/* Main Content */}
                    <div className="lg:col-span-3 space-y-4 sm:space-y-6 order-1 lg:order-2">
                        {/* Score Summary at Top */}
                        <div className="animate-fade-in-up">
                            <ScoreSummary scores={scores} totalResults={recommendations.length} currentPrompt={selectedPrompt} />
                        </div>

                        {/* Error State */}
                        {error && (
                            <div className="card-simple p-6 animate-fade-in-up">
                                <div className="flex items-start space-x-4">
                                    <div className="bg-red-100 p-2 rounded-lg">
                                        <AlertCircle className="text-red-600" size={20} />
                                    </div>
                                    <div className="flex-1">
                                        <h4 className="text-red-900 font-semibold mb-2">Service Unavailable</h4>
                                        <p className="text-red-700 text-sm mb-3">{error}</p>
                                        <div className="flex items-center space-x-2 text-xs text-red-600">
                                            <span>ðŸ’¡</span>
                                            <span>Try refreshing or selecting a different prompt</span>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Loading State */}
                        {loading && (
                            <div className="card-simple p-8 animate-fade-in-up">
                                <LoadingSpinner message={`Analyzing fonts... ${progress}%`} />
                                {progress > 0 && progress < 100 && (
                                    <div className="mt-6">
                                        <div className="progress-modern">
                                            <div
                                                className="progress-modern-fill"
                                                style={{ width: `${progress}%` }}
                                            ></div>
                                        </div>
                                        <p className="text-gray-600 text-sm mt-3 text-center">
                                            Processing recommendations... {progress}% complete
                                        </p>
                                    </div>
                                )}
                            </div>
                        )}

                        {/* Results */}
                        {!loading && !error && recommendations.length > 0 && (
                            <div className="card-simple p-6 animate-fade-in-up">
                                <FontRecommendations
                                    recommendations={recommendations}
                                    prompt={selectedPrompt}
                                    onScoreSaved={handleScoreSaved}
                                />
                            </div>
                        )}

                        {/* Empty State */}
                        {!loading && !error && recommendations.length === 0 && selectedPrompt && (
                            <div className="card-simple p-8 animate-fade-in-up">
                                <div className="text-center">
                                    <div className="bg-gray-100 rounded-full p-4 w-16 h-16 mx-auto mb-4">
                                        <TrendingUp className="text-gray-400 mx-auto" size={24} />
                                    </div>
                                    <h3 className="text-lg font-semibold text-gray-900 mb-2">No Results Found</h3>
                                    <p className="text-gray-600 text-sm">
                                        Try selecting a different prompt or refreshing the current one.
                                    </p>
                                </div>
                            </div>
                        )}

                        {/* Initial State */}
                        {!loading && !error && recommendations.length === 0 && !selectedPrompt && (
                            <div className="card-simple p-8 animate-fade-in-up">
                                <div className="text-center">
                                    <div className="bg-blue-100 rounded-full p-4 w-16 h-16 mx-auto mb-4">
                                        <Zap className="text-blue-600 mx-auto" size={24} />
                                    </div>
                                    <h3 className="text-lg font-semibold text-gray-900 mb-2">Welcome to Font Evaluator</h3>
                                    <p className="text-gray-600 text-sm mb-4">
                                        Select a prompt from the sidebar to start evaluating font recommendations.
                                    </p>
                                    <div className="lg:hidden">
                                        <button
                                            onClick={toggleMobileNav}
                                            className="btn-primary flex items-center justify-center space-x-2 px-4 py-2 rounded-xl text-sm font-medium"
                                        >
                                            <Menu className="w-4 h-4" />
                                            <span>Open Prompts</span>
                                        </button>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
