import React from 'react';

const LoadingSpinner = ({ message = "Loading..." }) => {
    return (
        <div className="flex flex-col items-center justify-center p-8">
            <div className="relative">
                {/* Outer ring */}
                <div className="w-16 h-16 border-4 border-purple-200/30 rounded-full"></div>
                {/* Animated ring */}
                <div className="absolute top-0 left-0 w-16 h-16 border-4 border-transparent border-t-purple-400 rounded-full animate-spin"></div>
                {/* Inner glow */}
                <div className="absolute inset-2 bg-gradient-to-r from-purple-400/20 to-pink-400/20 rounded-full animate-pulse"></div>
            </div>
            <p className="mt-6 text-purple-200 font-medium text-center">{message}</p>
            <div className="mt-4 flex space-x-1">
                <div className="w-2 h-2 bg-purple-400 rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-purple-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                <div className="w-2 h-2 bg-purple-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
            </div>
        </div>
    );
};

export default LoadingSpinner;
